CREATE FUNCTION public.check_period_open(p_company uuid, p_date date) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
    is_open boolean;
BEGIN
    SELECT ap.is_open INTO is_open
    FROM accounting_periods ap
    WHERE ap.company_id = p_company
      AND p_date BETWEEN ap.date_from AND ap.date_to
    LIMIT 1;

    IF is_open IS NULL THEN
        RAISE EXCEPTION 'No accounting period defined for %', p_date;
    END IF;

    IF NOT is_open THEN
        RAISE EXCEPTION 'Accounting period for % is closed', p_date;
    END IF;

    RETURN true;
END;
$$;


