CREATE FUNCTION public.company_currency(p_company uuid) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
  cur_currency uuid;
  tenant uuid;
  country uuid;
  tenant_default uuid;
  global_usd uuid;
BEGIN
  -- 1) explicit company setting
  SELECT currency_id, tenant_id, country_id INTO cur_currency, tenant, country
    FROM company_settings cs
    JOIN company c ON c.id = cs.company_id
    WHERE cs.company_id = p_company
    LIMIT 1;

  IF cur_currency IS NOT NULL THEN
    RETURN cur_currency;
  END IF;

  -- 2) tenant default currency (tenant_enabled_currencies where is_default)
  IF tenant IS NOT NULL THEN
    SELECT currency_id INTO tenant_default
      FROM tenant_enabled_currencies tec
      WHERE tec.tenant_id = tenant AND tec.is_default = true
      LIMIT 1;

    IF tenant_default IS NOT NULL THEN
      RETURN tenant_default;
    END IF;
  END IF;

  -- 3) country mapping (requires country_default_currency table)
  IF country IS NOT NULL THEN
    SELECT currency_id INTO cur_currency
      FROM country_default_currency cdc
      WHERE cdc.country_id = country AND cdc.is_active = true
      LIMIT 1;
    IF cur_currency IS NOT NULL THEN
      RETURN cur_currency;
    END IF;
  END IF;

  -- 4) tenant first enabled currency
  IF tenant IS NOT NULL THEN
    SELECT currency_id INTO tenant_default
      FROM tenant_enabled_currencies tec
      WHERE tec.tenant_id = tenant
      ORDER BY tec.created_at
      LIMIT 1;
    IF tenant_default IS NOT NULL THEN
      RETURN tenant_default;
    END IF;
  END IF;

  -- 5) global fallback (USD if present)
  SELECT id INTO global_usd FROM currencies WHERE code='USD' LIMIT 1;
  IF global_usd IS NOT NULL THEN
    RETURN global_usd;
  END IF;

  -- 6) absolute fallback: first active currency
  SELECT id INTO global_usd FROM currencies WHERE is_active = true LIMIT 1;
  RETURN global_usd;
END;
$$;


