CREATE FUNCTION public.compute_fx_difference(p_amount numeric, p_txn_currency text, p_company_currency text, p_txn_date date, p_reval_date date) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE
    original_rate numeric;
    reval_rate numeric;
BEGIN
    original_rate := fx_rate_lookup(p_txn_currency, p_company_currency, p_txn_date);
    reval_rate    := fx_rate_lookup(p_txn_currency, p_company_currency, p_reval_date);

    RETURN (p_amount * reval_rate) - (p_amount * original_rate);
END;
$$;


