CREATE FUNCTION public.ensure_followup_refs_are_valid() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  cnt int;
  has_tenant_col boolean;
BEGIN
  -- ensure referenced lead exists
  SELECT count(*) INTO cnt FROM public.lead WHERE id = NEW.lead_id;
  IF cnt = 0 THEN
    RAISE EXCEPTION 'referenced lead % does not exist', NEW.lead_id;
  END IF;

  -- check whether lead table has tenant_id column
  SELECT EXISTS (
    SELECT 1
    FROM information_schema.columns
    WHERE table_schema = 'public'
      AND table_name = 'lead'
      AND column_name = 'tenant_id'
  ) INTO has_tenant_col;

  IF has_tenant_col THEN
    SELECT count(*) INTO cnt FROM public.lead WHERE id = NEW.lead_id AND tenant_id = NEW.tenant_id;
    IF cnt = 0 THEN
      RAISE EXCEPTION 'lead % does not belong to tenant %', NEW.lead_id, NEW.tenant_id;
    END IF;
  END IF;

  RETURN NEW;
END;
$$;


