-- Name: evaluate_lead_assignment_rules(); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.evaluate_lead_assignment_rules() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  r RECORD;
  jrec RECORD;
  colval text;
  matches boolean;
  lead_tenant uuid;
  prev_owner uuid;
  new_owner uuid;
BEGIN
  -- determine tenant id on the lead if exists
  BEGIN
    lead_tenant := NEW.tenant_id;
  EXCEPTION WHEN undefined_column THEN
    lead_tenant := NULL;
  END;

  IF TG_OP = 'UPDATE' THEN
    prev_owner := OLD.owner_id;
  ELSE
    prev_owner := NULL;
  END IF;

  FOR r IN
    SELECT * FROM public.lead_assignment_rule
    WHERE active = true
      AND (tenant_id IS NULL OR tenant_id = lead_tenant)
    ORDER BY priority ASC
  LOOP
    matches := true;

    IF r.criteria IS NOT NULL THEN
      FOR jrec IN SELECT * FROM jsonb_each_text(r.criteria) LOOP
        IF NOT EXISTS (
          SELECT 1 FROM information_schema.columns
          WHERE table_schema = 'public' AND table_name = 'lead' AND column_name = jrec.key
        ) THEN
          matches := false;
          EXIT;
        END IF;

        colval := to_jsonb(NEW) ->> jrec.key;

        IF colval IS NULL AND jrec.value IS NOT NULL THEN
          matches := false;
          EXIT;
        END IF;

        IF colval IS NOT NULL AND colval IS DISTINCT FROM jrec.value THEN
          matches := false;
          EXIT;
        END IF;
      END LOOP;
    END IF;

    IF matches THEN
      new_owner := r.assign_to;

      IF (NEW.owner_id IS NULL) OR (NEW.owner_id IS DISTINCT FROM new_owner) THEN
        -- attempt to update lead owner (non-fatal)
        BEGIN
          UPDATE public.lead SET owner_id = new_owner WHERE id = NEW.id;
        EXCEPTION WHEN others THEN
          -- ignore
        END;

        -- record history (best-effort)
        BEGIN
          INSERT INTO public.lead_assignment_history(
            tenant_id, lead_id, rule_id, from_owner, to_owner, reason, created_by, created_at
          ) VALUES (
            lead_tenant, NEW.id, r.id, prev_owner, new_owner, NULL, NULL, now()
          );
        EXCEPTION WHEN others THEN
          -- ignore history insert errors
        END;
      END IF;

      RETURN NEW;
    END IF;
  END LOOP;

  RETURN NEW;
END;
$$;


