--

CREATE FUNCTION public.find_potential_lead_duplicates(p_lead_id uuid, p_limit integer DEFAULT 5) RETURNS TABLE(duplicate_lead_id uuid, score numeric)
    LANGUAGE sql
    AS $$
  SELECT l.id, GREATEST(similarity(l.name, base.name), similarity(l.primary_email, base.primary_email))::numeric AS score
  FROM public.lead base
  JOIN public.lead l ON l.id <> base.id
  WHERE base.id = p_lead_id
    AND base.tenant_id = l.tenant_id
    AND (similarity(l.name, base.name) > 0.25 OR similarity(l.primary_email, base.primary_email) > 0.25)
  ORDER BY score DESC
  LIMIT p_limit;
$$;


