-- TOC entry 828 (class 1255 OID 17400)
-- Name: fn_audit_log(); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.fn_audit_log() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_new jsonb;
  v_old jsonb;
  v_row jsonb;
  v_tenant uuid := NULL;
  v_operation text := TG_OP;
  v_table text := TG_TABLE_NAME;
  v_record uuid := NULL;
  v_actor uuid := NULL;
BEGIN
  -- Build JSONB versions of NEW/OLD safely
  IF TG_OP = 'INSERT' THEN
    v_new := to_jsonb(NEW);
    v_old := NULL;
  ELSIF TG_OP = 'UPDATE' THEN
    v_new := to_jsonb(NEW);
    v_old := to_jsonb(OLD);
  ELSIF TG_OP = 'DELETE' THEN
    v_new := NULL;
    v_old := to_jsonb(OLD);
  ELSE
    v_new := NULL;
    v_old := NULL;
  END IF;

  v_row := COALESCE(v_new, v_old);

  -- extract tenant_id if present and cast to uuid safely
  IF v_row IS NOT NULL AND (v_row ? 'tenant_id') THEN
    BEGIN
      v_tenant := NULLIF(v_row ->> 'tenant_id', '')::uuid;
    EXCEPTION WHEN OTHERS THEN
      v_tenant := NULL;
    END;
  END IF;

  -- extract record id if present and cast to uuid safely
  IF v_row IS NOT NULL AND (v_row ? 'id') THEN
    BEGIN
      v_record := NULLIF(v_row ->> 'id', '')::uuid;
    EXCEPTION WHEN OTHERS THEN
      v_record := NULL;
    END;
  END IF;

  -- best-effort: try to map current_user (role) to an app_user.id if email equals current_user
  -- if no mapping exists, actor remains NULL
  BEGIN
    SELECT id INTO v_actor FROM app_user WHERE email = current_user LIMIT 1;
  EXCEPTION WHEN OTHERS THEN
    v_actor := NULL;
  END;

  -- if audit_log table exists, attempt to insert a row (set tenant for RLS)
  IF EXISTS (
    SELECT 1 FROM information_schema.tables
    WHERE table_schema = current_schema() AND table_name = 'audit_log'
  ) THEN
    BEGIN
      IF v_tenant IS NOT NULL THEN
        PERFORM set_config('app.tenant_id', v_tenant::text, true);
      END IF;

      INSERT INTO audit_log (id, tenant_id, actor_id, table_name, record_id, operation, diff, created_at)
      VALUES (
        gen_random_uuid(),
        v_tenant,
        v_actor,
        v_table,
        v_record,
        v_operation,
        COALESCE(v_new, v_old),
        now()
      );
    EXCEPTION WHEN OTHERS THEN
