CREATE FUNCTION public.fn_post_invoice_lines(p_tenant uuid, p_company uuid, p_journal_id uuid, p_ref text, p_invoice_lines jsonb, p_type text) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_entry uuid;
  v_line jsonb;
  v_amount numeric;
  v_tax_map record;
  v_tax_amount numeric;
  v_ar_account uuid;
  v_ap_account uuid;
  v_result jsonb := '[]'::jsonb;
BEGIN
  -- create header
  v_entry := fn_create_journal_entry(p_tenant, p_company, p_journal_id, p_ref, now()::date);

  -- helpers: get AR/AP
  SELECT id INTO v_ar_account FROM public.accounts WHERE company_id = p_company AND code = '1000' LIMIT 1;
  SELECT id INTO v_ap_account FROM public.accounts WHERE company_id = p_company AND code = '2000' LIMIT 1;

  FOR v_line IN SELECT * FROM jsonb_array_elements(p_invoice_lines)
  LOOP
    v_amount := (v_line ->> 'qty')::numeric * (v_line ->> 'unit_price')::numeric;

    -- revenue/expense line
    IF p_type = 'out_invoice' THEN
      PERFORM fn_add_journal_line(v_entry, (v_line->>'account_id')::uuid, 0, v_amount, (v_line->>'partner_id')::uuid, NULL, NULL, (v_line->>'description'));
    ELSE
      PERFORM fn_add_journal_line(v_entry, (v_line->>'account_id')::uuid, v_amount, 0, (v_line->>'partner_id')::uuid, NULL, NULL, (v_line->>'description'));
    END IF;

    -- tax: lookup company_tax_maps for tax_type_id
    IF (v_line->>'tax_type_id') IS NOT NULL THEN
      SELECT ctm.account_id, tr.rate::numeric AS tax_rate
      INTO v_tax_map
      FROM public.company_tax_maps ctm
      JOIN public.tax_rates tr ON tr.id = ctm.tax_rate_id
      WHERE ctm.company_id = p_company
        AND ctm.tax_type_id = (v_line->>'tax_type_id')::uuid
        AND ctm.is_active = true
      LIMIT 1;

      IF FOUND THEN
        v_tax_amount := round((v_amount * (v_tax_map.tax_rate / 100.0))::numeric, 2);
        IF p_type = 'out_invoice' THEN
          PERFORM fn_add_journal_line(v_entry, v_tax_map.account_id, 0, v_tax_amount, (v_line->>'partner_id')::uuid, (v_line->>'tax_type_id')::uuid, NULL, 'Tax');
        ELSE
          PERFORM fn_add_journal_line(v_entry, v_tax_map.account_id, v_tax_amount, 0, (v_line->>'partner_id')::uuid, (v_line->>'tax_type_id')::uuid, NULL, 'Tax');
        END IF;
      ELSE
        -- no tax mapping: insert to suspense account
        PERFORM fn_add_journal_line(v_entry, (SELECT id FROM public.accounts WHERE company_id = p_company AND code = '9990' LIMIT 1), 0, 0, NULL, NULL, NULL, 'Tax mapping missing');
      END IF;
    END IF;

    -- append to result for logging
    v_result := v_result || jsonb_build_object('line', v_line, 'posted_to_entry', v_entry);
  END LOOP;

  -- create balancing AR/AP line
  -- compute totals
  PERFORM fn_post_journal_entry(v_entry);
  RETURN jsonb_build_object('entry_id', v_entry, 'lines', v_result);
END;
$$;


