CREATE FUNCTION public.fn_post_journal_entry(p_entry_id uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_total_debit numeric := 0;
  v_total_credit numeric := 0;
BEGIN
  SELECT COALESCE(SUM(debit),0) INTO v_total_debit FROM public.journal_entry_lines WHERE journal_entry_id = p_entry_id;
  SELECT COALESCE(SUM(credit),0) INTO v_total_credit FROM public.journal_entry_lines WHERE journal_entry_id = p_entry_id;

  IF abs(v_total_debit - v_total_credit) > 0.000001 THEN
    RAISE EXCEPTION 'Journal entry % not balanced: debit=% credit=%', p_entry_id, v_total_debit, v_total_credit;
  END IF;

  UPDATE public.journal_entries SET posted = true, posted_at = now() WHERE id = p_entry_id;
END;
$$;


