--
-- TOC entry 881 (class 1255 OID 21367)
-- Name: fx_rate_lookup(text, text, date); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.fx_rate_lookup(p_base text, p_quote text, p_date date DEFAULT (now())::date) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE
    base_id uuid;
    quote_id uuid;
    r numeric;
BEGIN
    SELECT id INTO base_id FROM currencies WHERE code=p_base;
    SELECT id INTO quote_id FROM currencies WHERE code=p_quote;

    IF base_id IS NULL OR quote_id IS NULL THEN
        RAISE EXCEPTION 'Unknown currency % or %', p_base, p_quote;
    END IF;

    -- direct rate
    SELECT rate INTO r FROM fx_rates
    WHERE base_currency=base_id AND quote_currency=quote_id AND rate_date=p_date;

    IF r IS NOT NULL THEN RETURN r; END IF;

    -- try previous date
    SELECT rate INTO r FROM fx_rates
    WHERE base_currency=base_id AND quote_currency=quote_id
    ORDER BY rate_date DESC LIMIT 1;

    IF r IS NOT NULL THEN RETURN r; END IF;

    RAISE EXCEPTION 'No FX rate found for %/% on %', p_base, p_quote, p_date;
END;
$$;


