CREATE FUNCTION public.generate_journal_sequence(p_journal uuid) RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
    seq record;
    formatted text;
BEGIN
    SELECT * INTO seq FROM journal_sequences WHERE journal_id = p_journal FOR UPDATE;

    IF NOT FOUND THEN
        RAISE EXCEPTION 'Sequence not found for journal %', p_journal;
    END IF;

    formatted := seq.prefix || LPAD(seq.next_number::text, seq.padding, '0');

    UPDATE journal_sequences
    SET next_number = seq.next_number + 1
    WHERE id = seq.id;

    RETURN formatted;
END;
$$;


