CREATE FUNCTION public.get_applicable_taxes(p_tenant uuid, p_company uuid, p_country text, p_state text, p_city text, p_name text DEFAULT NULL::text, p_when date DEFAULT CURRENT_DATE) RETURNS TABLE(tax_rate_id uuid, name text, rate numeric, kind text, is_compound boolean, priority integer, jurisdiction_level text)
    LANGUAGE plpgsql STABLE
    AS $$
BEGIN
  RETURN QUERY
  SELECT tr.id, tr.name, tr.rate, tr.kind, tr.is_compound, tr.priority,
    CASE
      WHEN tr.company_id IS NOT NULL THEN 'company'
      WHEN tr.tenant_id IS NOT NULL AND tr.company_id IS NULL THEN 'tenant'
      WHEN tr.country IS NOT NULL AND tr.country <> '' THEN 'country'
      WHEN tr.state IS NOT NULL AND tr.state <> '' THEN 'state'
      WHEN tr.city IS NOT NULL AND tr.city <> '' THEN 'city'
      ELSE 'global'
    END
  FROM public.tax_rates tr
  WHERE tr.is_deleted = false
    AND tr.active = true
    AND (tr.tenant_id = p_tenant OR tr.tenant_id IS NULL)
    AND (tr.company_id = p_company OR tr.company_id IS NULL)
    AND (tr.country IS NULL OR tr.country = '' OR lower(tr.country) = lower(coalesce(p_country,'')))
    AND (tr.state IS NULL OR tr.state = '' OR lower(tr.state) = lower(coalesce(p_state,'')))
    AND (tr.city IS NULL OR tr.city = '' OR lower(tr.city) = lower(coalesce(p_city,'')))
    AND (p_name IS NULL OR tr.name_lc = lower(p_name))
    AND ( (tr.effective_from IS NULL OR tr.effective_from <= p_when)
       AND (tr.effective_to IS NULL OR tr.effective_to >= p_when) )
  ORDER BY tr.priority ASC, tr.priority IS NULL, tr.rate DESC;
END;
$$;


