  FROM public.global_settings gs
  WHERE gs.key = p_key
    AND gs.tenant_id IS NOT DISTINCT FROM p_tenant_id
    AND gs.company_id IS NULL
  ORDER BY gs.version DESC LIMIT 1;

  IF FOUND THEN
    RETURN;
  END IF;

  -- 3. global default
  RETURN QUERY
  SELECT gs.value, 'global'::text, NULL::uuid
  FROM public.global_settings gs
  WHERE gs.key = p_key
    AND gs.tenant_id IS NULL
    AND gs.company_id IS NULL
  ORDER BY gs.version DESC LIMIT 1;

  -- if not found, returns no rows (caller should handle NULL)
