CREATE FUNCTION public.hms_next_invoice_number(tid uuid, cid uuid, prefix text DEFAULT ''::text) RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
  v bigint;
BEGIN
  LOOP
    UPDATE hms_invoice_sequence SET last_number = last_number + 1 WHERE tenant_id = tid AND company_id = cid;
