--
-- TOC entry 855 (class 1255 OID 19778)
-- Name: hms_receipt_line_to_ledger_and_stock(); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.hms_receipt_line_to_ledger_and_stock() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  loc_to uuid := NULL;
  computed_unit_cost numeric := 0;
  computed_total_cost numeric := 0;
BEGIN
  -- determine target location: prefer provided location else default for company
  IF NEW.location_id IS NOT NULL THEN
    loc_to := NEW.location_id;
  ELSE
    SELECT id INTO loc_to FROM public.hms_stock_locations
      WHERE company_id = NEW.company_id AND is_default = true LIMIT 1;
  END IF;

  -- compute unit cost (prefer receipt unit_price, then product.default_cost, then batch.cost)
  IF NEW.unit_price IS NOT NULL AND NEW.unit_price <> 0 THEN
    computed_unit_cost := NEW.unit_price;
  ELSE
    SELECT COALESCE(default_cost,0) INTO computed_unit_cost FROM public.hms_product WHERE id = NEW.product_id LIMIT 1;
    IF computed_unit_cost IS NULL OR computed_unit_cost = 0 THEN
      SELECT cost INTO computed_unit_cost FROM public.hms_product_batch WHERE id = NEW.batch_id LIMIT 1;
    END IF;
    computed_unit_cost := COALESCE(computed_unit_cost,0);
  END IF;

  computed_total_cost := computed_unit_cost * COALESCE(NEW.qty,0);

  -- insert stock ledger (immutable)
  INSERT INTO public.hms_stock_ledger (
    tenant_id, company_id, product_id, related_type, related_id, movement_type,
    qty, uom, unit_cost, total_cost, from_location_id, to_location_id,
    batch_id, lot_number, reference, created_at, metadata
  ) VALUES (
    NEW.tenant_id, NEW.company_id, NEW.product_id, 'purchase_receipt', NEW.receipt_id, 'in',
    NEW.qty, NEW.uom, computed_unit_cost, computed_total_cost, NULL, loc_to,
    NEW.batch_id, NEW.lot_number, NULL, now(), NEW.metadata
  );

  -- upsert aggregate stock_levels
  LOOP
    UPDATE public.hms_stock_levels
      SET quantity = quantity + NEW.qty,
          updated_at = now()
    WHERE tenant_id = NEW.tenant_id
      AND company_id = NEW.company_id
      AND product_id = NEW.product_id
      AND (batch_id IS NOT DISTINCT FROM NEW.batch_id)
      AND location_id = COALESCE(loc_to, NEW.location_id);

    IF FOUND THEN
      EXIT;
    END IF;

    BEGIN
      INSERT INTO public.hms_stock_levels (id, tenant_id, company_id, product_id, batch_id, location_id, quantity, updated_at, metadata)
      VALUES (gen_random_uuid(), NEW.tenant_id, NEW.company_id, NEW.product_id, NEW.batch_id, COALESCE(loc_to, NEW.location_id), NEW.qty, now(), NEW.metadata);
      EXIT;
    EXCEPTION WHEN unique_violation THEN
      -- concurrent upsert, loop and try update again
    END;
  END LOOP;

  -- update received_qty on PO line if referenced
  IF NEW.po_line_id IS NOT NULL THEN
    UPDATE public.hms_purchase_order_line
      SET received_qty = COALESCE(received_qty,0) + NEW.qty,
          updated_at = now()
    WHERE id = NEW.po_line_id;
  END IF;

  RETURN NEW;
END;
$$;


