CREATE FUNCTION public.install_company_accounting_settings(p_tenant uuid, p_company uuid, p_country uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    fy_start date;
    fy_end date;

    currency uuid;
    ar uuid;
    ap uuid;
    sales uuid;
    purch uuid;
    out_tax uuid;
    in_tax uuid;

    sj uuid;
    pj uuid;
    cj uuid;
    bj uuid;
    gj uuid;

    def_tax uuid;
BEGIN
    SELECT start_date,end_date INTO fy_start,fy_end FROM compute_fiscal_year();

    SELECT id INTO currency FROM currencies ORDER BY is_default DESC LIMIT 1;

    SELECT id INTO ar    FROM account_chart WHERE company_id=p_company AND code='120000';
    SELECT id INTO ap    FROM account_chart WHERE company_id=p_company AND code='210000';
    SELECT id INTO sales FROM account_chart WHERE company_id=p_company AND code='410000';
    SELECT id INTO purch FROM account_chart WHERE company_id=p_company AND code='510000';
    SELECT id INTO out_tax FROM account_chart WHERE company_id=p_company AND code='231000';
    SELECT id INTO in_tax  FROM account_chart WHERE company_id=p_company AND code='571000';

    SELECT id INTO sj FROM journals WHERE company_id=p_company AND code='SJ';
    SELECT id INTO pj FROM journals WHERE company_id=p_company AND code='PJ';
    SELECT id INTO cj FROM journals WHERE company_id=p_company AND code='CJ';
    SELECT id INTO bj FROM journals WHERE company_id=p_company AND code='BJ';
    SELECT id INTO gj FROM journals WHERE company_id=p_company AND code='GJ';

    SELECT tax_rate_id INTO def_tax 
    FROM company_tax_maps WHERE company_id=p_company AND is_active=true LIMIT 1;

    INSERT INTO company_accounting_settings (
        tenant_id, company_id,
        default_sale_tax_id, default_purchase_tax_id,
        currency_id, currency_precision, rounding_method,
        fiscal_year_start, fiscal_year_end,
        ar_account_id, ap_account_id,
        sales_account_id, purchase_account_id,
        output_tax_account_id, input_tax_account_id,
        sales_journal_id, purchase_journal_id,
        cash_journal_id, bank_journal_id, general_journal_id,
        localization
    )
    VALUES (
        p_tenant, p_company,
        def_tax, def_tax,
        currency,2,'ROUND_HALF_UP',
        fy_start,fy_end,
        ar,ap,sales,purch,out_tax,in_tax,
        sj,pj,cj,bj,gj,
        (SELECT code FROM countries WHERE id=p_country)
    )
    ON CONFLICT(company_id) DO NOTHING;
END;
$$;


