--

CREATE FUNCTION public.install_default_fiscal_positions(p_tenant uuid, p_company uuid, p_country uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    fp_domestic uuid;
    fp_export uuid;
    fp_reverse uuid;

    zero_tax uuid;
    std_tax uuid;
BEGIN
    SELECT id INTO zero_tax FROM tax_rates WHERE rate_value=0 LIMIT 1;
    SELECT id INTO std_tax  FROM tax_rates WHERE rate_value>0 ORDER BY rate_value ASC LIMIT 1;

    INSERT INTO fiscal_positions (tenant_id,company_id,name,is_default)
    VALUES (p_tenant,p_company,'Domestic Transactions',true)
    RETURNING id INTO fp_domestic;

    INSERT INTO fiscal_positions (tenant_id,company_id,name)
    VALUES (p_tenant,p_company,'Export / Zero Rated')
    RETURNING id INTO fp_export;

    INSERT INTO fiscal_positions (tenant_id,company_id,name)
    VALUES (p_tenant,p_company,'Reverse Charge')
    RETURNING id INTO fp_reverse;

    IF std_tax IS NOT NULL THEN
        INSERT INTO fiscal_position_tax_map (fiscal_position_id,from_tax_id,to_tax_id)
        VALUES (fp_domestic,std_tax,std_tax)
        ON CONFLICT DO NOTHING;
    END IF;

    IF zero_tax IS NOT NULL AND std_tax IS NOT NULL THEN
        INSERT INTO fiscal_position_tax_map (fiscal_position_id,from_tax_id,to_tax_id)
        VALUES (fp_export,std_tax,zero_tax)
        ON CONFLICT DO NOTHING;
    END IF;

    IF std_tax IS NOT NULL THEN
        INSERT INTO fiscal_position_tax_map (fiscal_position_id,from_tax_id,to_tax_id,condition)
        VALUES (fp_reverse,std_tax,std_tax,'{"reverse_charge":true}')
        ON CONFLICT DO NOTHING;
    END IF;
END;
$$;


