CREATE FUNCTION public.install_tax_gl_mappings(p_tenant uuid, p_company uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    out_acc uuid;
    in_acc uuid;
    rc_acc uuid;
    tr record;
BEGIN
    SELECT id INTO out_acc FROM account_chart WHERE company_id=p_company AND code='231000';
    SELECT id INTO in_acc  FROM account_chart WHERE company_id=p_company AND code='571000';
    rc_acc := out_acc;

    FOR tr IN SELECT tax_rate_id FROM company_tax_maps WHERE company_id=p_company LOOP
        INSERT INTO tax_gl_accounts (tenant_id,company_id,tax_rate_id,output_account_id,input_account_id,reverse_charge_account_id)
        VALUES (p_tenant,p_company,tr.tax_rate_id,out_acc,in_acc,rc_acc)
        ON CONFLICT(company_id,tax_rate_id) DO NOTHING;

        IF (SELECT rate_value FROM tax_rates WHERE id=tr.tax_rate_id)=0
        THEN
            INSERT INTO tax_posting_rules (tenant_id,company_id,tax_rate_id,behavior)
            VALUES (p_tenant,p_company,tr.tax_rate_id,'zero_rated')
            ON CONFLICT DO NOTHING;
        ELSE
            INSERT INTO tax_posting_rules (tenant_id,company_id,tax_rate_id,behavior)
            VALUES (p_tenant,p_company,tr.tax_rate_id,'standard_output')
            ON CONFLICT DO NOTHING;

            INSERT INTO tax_posting_rules (tenant_id,company_id,tax_rate_id,behavior)
            VALUES (p_tenant,p_company,tr.tax_rate_id,'standard_input')
            ON CONFLICT DO NOTHING;
        END IF;
    END LOOP;
END;
$$;


