--
-- TOC entry 899 (class 1255 OID 23039)
-- Name: pharmacy_fulfill_order(uuid, uuid, uuid, uuid, uuid, uuid, uuid, jsonb, jsonb); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.pharmacy_fulfill_order(p_tenant_id uuid, p_company_id uuid, p_created_by uuid, p_patient_id uuid, p_encounter_id uuid DEFAULT NULL::uuid, p_prescription_id uuid DEFAULT NULL::uuid, p_location_id uuid DEFAULT NULL::uuid, p_items jsonb DEFAULT '[]'::jsonb, p_payment jsonb DEFAULT NULL::jsonb) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_invoice_id uuid := gen_random_uuid();
  v_invoice_number text;
  v_line_idx int := 0;
  v_total numeric := 0;
  v_now timestamptz := now();
  v_stock_moves jsonb := '[]'::jsonb;

  v_item jsonb;
  v_product_id uuid;
  v_batch_id uuid;
  v_qty numeric;
  v_unit_price numeric;
  v_discount numeric;
  v_tax_rate numeric;
  v_tax_amount numeric;
  v_net numeric;
  v_uom text;
  v_available numeric;

  v_stock_move_id uuid;
  v_payment_id uuid;
BEGIN
  -- basic validation
  IF jsonb_typeof(p_items) IS DISTINCT FROM 'array' THEN
    RAISE EXCEPTION 'p_items must be a json array';
  END IF;

  v_invoice_number := public.hms_invoice_next_number(p_tenant_id, p_company_id);

  INSERT INTO public.hms_invoice (
    id, tenant_id, company_id, patient_id, encounter_id, invoice_number,
    issued_at, currency, subtotal, total_tax, total_discount, total,
    status, line_items, billing_metadata, locked,
    created_by, created_at, updated_at, outstanding, invoice_no
  )
  VALUES (
    v_invoice_id, p_tenant_id, p_company_id, p_patient_id, p_encounter_id,
    v_invoice_number, v_now, 'INR',
    0,0,0,0,'draft',
    '[]','{}',false,
    p_created_by, v_now, v_now, 0, v_invoice_number
  );

  -- iterate items
  FOR v_item IN SELECT * FROM jsonb_array_elements(p_items)
  LOOP
    v_line_idx := v_line_idx + 1;

    v_product_id := (v_item->>'product_id')::uuid;
    v_batch_id   := NULLIF(v_item->>'batch_id','')::uuid;
    v_qty        := COALESCE((v_item->>'quantity')::numeric,0);
    v_unit_price := COALESCE((v_item->>'unit_price')::numeric,0);
    v_discount   := COALESCE((v_item->>'discount_amount')::numeric,0);
    v_tax_rate   := COALESCE((v_item->>'tax_rate')::numeric,0);

    IF v_qty <= 0 THEN
      RAISE EXCEPTION 'Invalid qty for product %', v_product_id;
    END IF;

    -- choose batch when missing
    IF v_batch_id IS NULL THEN
      SELECT id INTO v_batch_id
      FROM public.hms_product_batch
      WHERE tenant_id = p_tenant_id
        AND company_id = p_company_id
        AND product_id = v_product_id
        AND qty_on_hand >= v_qty
      ORDER BY expiry_date NULLS LAST
      LIMIT 1;

      IF v_batch_id IS NULL THEN
        RAISE EXCEPTION 'No batch with enough qty for product %', v_product_id;
      END IF;
    ELSE
      -- verify provided batch belongs to product/tenant/company
      PERFORM 1 FROM public.hms_product_batch
       WHERE id = v_batch_id AND product_id = v_product_id AND tenant_id = p_tenant_id AND company_id = p_company_id;
      IF NOT FOUND THEN
        RAISE EXCEPTION 'Provided batch % invalid for product %', v_batch_id, v_product_id;
      END IF;
    END IF;

    -- check location stock
    SELECT quantity INTO v_available
    FROM public.hms_stock_levels
    WHERE tenant_id = p_tenant_id
      AND company_id = p_company_id
      AND product_id = v_product_id
      AND (batch_id IS NOT DISTINCT FROM v_batch_id)
      AND location_id = p_location_id;

    IF v_available IS NULL OR v_available < v_qty THEN
      RAISE EXCEPTION 'Insufficient stock at location % for product %', p_location_id, v_product_id;
    END IF;

    -- compute tax & net
    v_tax_amount := (v_tax_rate / 100.0) * (v_unit_price * v_qty - v_discount);
    v_net := (v_unit_price * v_qty) - v_discount + v_tax_amount;
    v_total := v_total + v_net;

    SELECT uom INTO v_uom FROM public.hms_product WHERE id = v_product_id;
    IF v_uom IS NULL THEN v_uom := 'each'; END IF;

    INSERT INTO public.hms_invoice_lines(
      id, tenant_id, company_id, invoice_id, line_idx, product_id,
      quantity, uom, unit_price, discount_amount, tax_amount,
      net_amount, created_at, subtotal
    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id, v_invoice_id, v_line_idx,
      v_product_id, v_qty, v_uom, v_unit_price, v_discount,
      v_tax_amount, v_net, v_now, (v_unit_price*v_qty - v_discount)
    );

    -- stock move (outgoing)
    INSERT INTO public.hms_stock_move (
      id, tenant_id, company_id, product_id, location_from,
      lot_id, qty, uom, move_type,
      source, source_reference, created_by, created_at
    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id,
      v_product_id, p_location_id,
      v_batch_id, v_qty, v_uom, 'out',
      'pharmacy_sale', v_invoice_id, p_created_by, v_now
    )
    RETURNING id INTO v_stock_move_id;

    v_stock_moves := v_stock_moves ||
      jsonb_build_object('stock_move_id', v_stock_move_id, 'product_id', v_product_id, 'qty', v_qty);

    -- decrement stock_levels
    UPDATE public.hms_stock_levels
    SET quantity = quantity - v_qty, updated_at = now()
    WHERE tenant_id = p_tenant_id
      AND company_id = p_company_id
      AND product_id = v_product_id
      AND (batch_id IS NOT DISTINCT FROM v_batch_id)
      AND location_id = p_location_id;

    IF NOT FOUND THEN
      RAISE EXCEPTION 'stock_levels row missing';
    END IF;

    -- decrement batch qty_on_hand
    UPDATE public.hms_product_batch
    SET qty_on_hand = qty_on_hand - v_qty
    WHERE id = v_batch_id;

    -- stock ledger
    INSERT INTO public.hms_stock_ledger(
      id, tenant_id, company_id, product_id,
      related_type, related_id, movement_type,
      qty, uom, from_location_id,
      batch_id, reference, created_at
    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id, v_product_id,
      'pharmacy_invoice', v_invoice_id, 'out',
      v_qty, v_uom, p_location_id,
      v_batch_id, v_invoice_number, v_now
    );
  END LOOP;

  -- finalize invoice
  UPDATE public.hms_invoice
  SET subtotal = COALESCE((SELECT SUM(subtotal) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total_tax = COALESCE((SELECT SUM(tax_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total_discount = COALESCE((SELECT SUM(discount_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total = COALESCE((SELECT SUM(net_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      outstanding = COALESCE((SELECT SUM(net_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      status = 'posted',
      updated_at = now()
  WHERE id = v_invoice_id;

  -- optional payment
  IF p_payment IS NOT NULL THEN
    v_payment_id := gen_random_uuid();
    INSERT INTO public.hms_invoice_payments (
      id, tenant_id, company_id, invoice_id,
      payment_reference, paid_at, amount,
      currency, method, metadata,
      created_by, created_at
    )
    VALUES (
      v_payment_id, p_tenant_id, p_company_id, v_invoice_id,
      (p_payment->>'reference'), v_now,
      (p_payment->>'amount')::numeric,
      'INR', (p_payment->>'method')::public.hms_payment_method,
      '{}'::jsonb, p_created_by, v_now
    );

    -- update invoice paid/outstanding (simple)
    UPDATE public.hms_invoice
      SET total_paid = COALESCE(total_paid,0) + (p_payment->>'amount')::numeric,
          outstanding = (COALESCE(total,0) - (COALESCE(total_paid,0) + (p_payment->>'amount')::numeric))
      WHERE id = v_invoice_id;
  END IF;

  RETURN jsonb_build_object(
    'invoice_id', v_invoice_id,
    'invoice_number', v_invoice_number,
    'total', (SELECT total FROM public.hms_invoice WHERE id = v_invoice_id),
    'stock_moves', v_stock_moves,
    'payment_id', v_payment_id
  );
END;
$$;


