    -- Try to insert a row to claim the key. If another transaction already created it, this does nothing.
    INSERT INTO public.hms_idempotency_keys (tenant_id, company_id, key, created_at)
    VALUES (p_tenant_id, p_company_id, p_idempotency_key, v_now)
    ON CONFLICT (tenant_id, company_id, key) DO NOTHING;

    -- Now lock the row for this key to serialize concurrent callers. If result already present, return it.
