    RAISE EXCEPTION 'p_items must be a json array';
  END IF;

  -- invoice number generator must exist (hms_invoice_next_number)
  v_invoice_number := public.hms_invoice_next_number(p_tenant_id, p_company_id);

  INSERT INTO public.hms_invoice (
