      FROM public.hms_product_batch
      WHERE tenant_id = p_tenant_id
        AND company_id = p_company_id
        AND product_id = v_product_id
        AND qty_on_hand >= v_qty
      ORDER BY expiry_date NULLS LAST
      LIMIT 1;

      IF v_batch_id IS NULL THEN
        RAISE EXCEPTION 'No batch with enough qty for product %', v_product_id;
      END IF;
    ELSE
      -- verify provided batch belongs to product/tenant/company
      PERFORM 1 FROM public.hms_product_batch
       WHERE id = v_batch_id AND product_id = v_product_id AND tenant_id = p_tenant_id AND company_id = p_company_id;
      IF NOT FOUND THEN
        RAISE EXCEPTION 'Provided batch % invalid for product %', v_batch_id, v_product_id;
      END IF;
    END IF;

    -- check location stock
    SELECT quantity INTO v_available
    FROM public.hms_stock_levels
