    v_net := (v_unit_price * v_qty) - v_discount + v_tax_amount;
    v_total := v_total + v_net;

    SELECT uom INTO v_uom FROM public.hms_product WHERE id = v_product_id;
    IF v_uom IS NULL THEN v_uom := 'each'; END IF;

