    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id, v_invoice_id, v_line_idx,
      v_product_id, v_qty, v_uom, v_unit_price, v_discount,
      v_tax_amount, v_net, v_now, (v_unit_price*v_qty - v_discount)
    );

    -- stock move (outgoing)
    INSERT INTO public.hms_stock_move (
      id, tenant_id, company_id, product_id, location_from,
      lot_id, qty, uom, move_type,
      source, source_reference, created_by, created_at
    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id,
      v_product_id, p_location_id,
      v_batch_id, v_qty, v_uom, 'out',
      'pharmacy_sale', v_invoice_id, p_created_by, v_now
    )
    RETURNING id INTO v_stock_move_id;

    v_stock_moves := v_stock_moves ||
      jsonb_build_object('stock_move_id', v_stock_move_id, 'product_id', v_product_id, 'qty', v_qty);

    -- decrement stock_levels
    UPDATE public.hms_stock_levels
    SET quantity = quantity - v_qty, updated_at = now()
    WHERE tenant_id = p_tenant_id
      AND company_id = p_company_id
      AND product_id = v_product_id
      AND (batch_id IS NOT DISTINCT FROM v_batch_id)
      AND location_id = p_location_id;

    IF NOT FOUND THEN
      RAISE EXCEPTION 'stock_levels row missing';
    END IF;

    -- decrement batch qty_on_hand
    UPDATE public.hms_product_batch
    SET qty_on_hand = qty_on_hand - v_qty
    WHERE id = v_batch_id;

    -- stock ledger
    INSERT INTO public.hms_stock_ledger(
      id, tenant_id, company_id, product_id,
      related_type, related_id, movement_type,
      qty, uom, from_location_id,
      batch_id, reference, created_at
    )
    VALUES (
      gen_random_uuid(), p_tenant_id, p_company_id, v_product_id,
      'pharmacy_invoice', v_invoice_id, 'out',
      v_qty, v_uom, p_location_id,
      v_batch_id, v_invoice_number, v_now
    );
  END LOOP;

  -- finalize invoice
  UPDATE public.hms_invoice
  SET subtotal = COALESCE((SELECT SUM(subtotal) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total_tax = COALESCE((SELECT SUM(tax_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total_discount = COALESCE((SELECT SUM(discount_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      total = COALESCE((SELECT SUM(net_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      outstanding = COALESCE((SELECT SUM(net_amount) FROM public.hms_invoice_lines WHERE invoice_id = v_invoice_id),0),
      status = 'posted',
      updated_at = now()
  WHERE id = v_invoice_id;

  -- optional payment
  IF p_payment IS NOT NULL THEN
    v_payment_id := gen_random_uuid();
    INSERT INTO public.hms_invoice_payments (
      id, tenant_id, company_id, invoice_id,
      payment_reference, paid_at, amount,
      currency, method, metadata,
      created_by, created_at
    )
    VALUES (
      v_payment_id, p_tenant_id, p_company_id, v_invoice_id,
      (p_payment->>'reference'), v_now,
      (p_payment->>'amount')::numeric,
      'INR', (p_payment->>'method')::public.hms_payment_method,
      '{}'::jsonb, p_created_by, v_now
    );

    -- update invoice paid/outstanding (simple)
    UPDATE public.hms_invoice
      SET total_paid = COALESCE(total_paid,0) + (p_payment->>'amount')::numeric,
          outstanding = (COALESCE(total,0) - (COALESCE(total_paid,0) + (p_payment->>'amount')::numeric))
      WHERE id = v_invoice_id;
  END IF;

  -- build result json
  v_result := jsonb_build_object(
    'invoice_id', v_invoice_id::text,
    'invoice_number', v_invoice_number,
    'total', (SELECT total FROM public.hms_invoice WHERE id = v_invoice_id),
    'stock_moves', v_stock_moves,
    'payment_id', v_payment_id
  );

  -- store result into idempotency table if key provided (so future calls return same result)
  IF p_idempotency_key IS NOT NULL THEN
    UPDATE public.hms_idempotency_keys
      SET result = v_result, created_at = now()
    WHERE tenant_id = p_tenant_id AND company_id = p_company_id AND key = p_idempotency_key;
  END IF;

  RETURN v_result;
END;
$$;


