CREATE FUNCTION public.post_customer_invoice(p_invoice_id uuid) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    inv record;
    ar uuid;
    sales uuid;
    taxrec record;
    je uuid;
    lines jsonb := '[]'::jsonb;
BEGIN
    SELECT * INTO inv FROM invoices WHERE id = p_invoice_id;

    -- Accounts
    SELECT ar_account_id INTO ar FROM company_accounting_settings WHERE company_id = inv.company_id;
    SELECT sales_account_id INTO sales FROM company_accounting_settings WHERE company_id = inv.company_id;

    -- Add revenue line
    lines := lines || jsonb_build_object(
        'account', sales,
        'debit', 0,
        'credit', inv.subtotal,
        'analytic_id', inv.analytic_account_id
    );

    -- Add tax lines
    FOR taxrec IN
        SELECT * FROM invoice_taxes WHERE invoice_id = p_invoice_id
    LOOP
        lines := lines || jsonb_build_object(
            'account', (
                SELECT output_account_id
                FROM tax_gl_accounts
                WHERE tax_rate_id = taxrec.tax_rate_id
                  AND company_id = inv.company_id
            ),
            'debit', 0,
            'credit', taxrec.tax_amount,
            'tax_rate_id', taxrec.tax_rate_id,
            'is_tax_line', true
        );
    END LOOP;

    -- AR line
    lines := lines || jsonb_build_object(
        'account', ar,
        'debit', inv.total,
        'credit', 0
    );

    je := post_ledger_entry(
        inv.tenant_id,
        inv.company_id,
        inv.journal_id,
        inv.invoice_date,
        inv.currency,
        inv.invoice_number,
        lines
    );

    UPDATE invoices SET journal_entry_id = je WHERE id = p_invoice_id;

    RETURN je;
END;
$$;


