-- Name: post_ledger_entry(uuid, uuid, uuid, date, text, text, jsonb); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.post_ledger_entry(p_tenant uuid, p_company uuid, p_journal uuid, p_date date, p_currency text, p_ref text, p_lines jsonb) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    je_id uuid := gen_random_uuid();
    line jsonb;
    base_currency text;
    fx numeric := 1;
    currency_id uuid;
    seq text;
    total_debit numeric := 0;
    total_credit numeric := 0;
BEGIN
    -- Company currency
    SELECT c.code INTO base_currency
    FROM company_accounting_settings cas
    JOIN currencies c ON c.id = cas.currency_id
    WHERE company_id = p_company;

    SELECT id INTO currency_id FROM currencies WHERE code = p_currency;

    -- FX rate if needed
    IF p_currency <> base_currency THEN
        fx := fx_rate_lookup(p_currency, base_currency, p_date);
    END IF;

    -- Generate sequence number
    seq := generate_journal_sequence(p_journal);

    -- Create Journal Entry
    INSERT INTO journal_entries (
        id, tenant_id, company_id, journal_id, entry_number,
        entry_date, currency_id, fx_rate, reference,
        created_at, updated_at
    )
    VALUES (
        je_id, p_tenant, p_company, p_journal, seq,
        p_date, currency_id, fx, p_ref,
        now(), now()
    );

    -- Insert Lines
    FOR line IN SELECT * FROM jsonb_array_elements(p_lines)
    LOOP
        INSERT INTO journal_entry_lines (
            id, journal_entry_id, tenant_id, company_id,
            account_id, debit, credit,
            tax_rate_id, analytic_account_id, is_tax_line,
            created_at
        )
        VALUES (
            gen_random_uuid(),
            je_id,
            p_tenant,
            p_company,
            (line->>'account')::uuid,
            (line->>'debit')::numeric * fx,
            (line->>'credit')::numeric * fx,
            (line->>'tax_rate_id')::uuid,
            (line->>'analytic_id')::uuid,
            COALESCE((line->>'is_tax_line')::boolean, false),
            now()
        );

        total_debit := total_debit + COALESCE((line->>'debit')::numeric * fx,0);
        total_credit := total_credit + COALESCE((line->>'credit')::numeric * fx,0);
    END LOOP;

    -- Double-entry validation
    IF round(total_debit - total_credit, 2) <> 0 THEN
        RAISE EXCEPTION 'Ledger imbalance: debit %, credit %', total_debit, total_credit;
    END IF;

    RETURN je_id;
END;
$$;


