CREATE FUNCTION public.post_payment(p_payment_id uuid, p_payment_type text) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    p           record;       -- payment data
    je          uuid;
    lines       jsonb := '[]'::jsonb;

    ar_acc      uuid;
    ap_acc      uuid;
    bank_acc    uuid;
    cash_acc    uuid;

    base_currency text;
    fx numeric := 1;
BEGIN
    -- Fetch payment
    IF p_payment_type = 'customer' THEN
        SELECT * INTO p FROM customer_payment WHERE id=p_payment_id;
    ELSE
        SELECT * INTO p FROM vendor_payment WHERE id=p_payment_id;
    END IF;

    IF NOT FOUND THEN
        RAISE EXCEPTION 'Payment % not found', p_payment_id;
    END IF;

    -- Company accounts
    SELECT 
        ar_account_id,
        ap_account_id,
        bank_journal_id,
        cash_journal_id
    INTO
        ar_acc, ap_acc, bank_acc, cash_acc
    FROM company_accounting_settings
    WHERE company_id = p.company_id;

    -- Company base currency
    SELECT c.code INTO base_currency
    FROM currencies c
    JOIN company_accounting_settings cas ON cas.currency_id=c.id
    WHERE cas.company_id = p.company_id;

    -- FX
    IF p.currency <> base_currency THEN
        fx := fx_rate_lookup(
            p.currency,
            base_currency,
            p.payment_date
        );
    END IF;

    -- ============================================================
    -- BANK/CASH LINE (incoming or outgoing)
    -- ============================================================
    DECLARE journal_acc uuid;
    BEGIN
        IF p.journal_type = 'BANK' THEN
            journal_acc := (
                SELECT default_account_id 
                FROM journals WHERE id = p.journal_id
            );
        ELSE
            journal_acc := (
                SELECT default_account_id 
                FROM journals WHERE id = p.journal_id
            );
        END IF;

        IF p_payment_type = 'customer' THEN
            lines := lines || jsonb_build_object(
                'account', journal_acc,
                'debit', (p.amount * fx),
                'credit', 0
            );
        ELSE
            lines := lines || jsonb_build_object(
                'account', journal_acc,
                'debit', 0,
                'credit', (p.amount * fx)
            );
        END IF;
    END;

    -- ============================================================
    -- ALLOCATE to documents (invoices / vendor bills)
    -- ============================================================
    DECLARE alloc record;
    BEGIN
        FOR alloc IN 
            SELECT * FROM payment_allocations
            WHERE payment_id = p_payment_id
        LOOP
            IF alloc.document_type = 'invoice' THEN
                -- POST CUSTOMER PAYMENT
                lines := lines || jsonb_build_object(
                    'account', ar_acc,
                    'debit', 0,
                    'credit', (alloc.allocated_amount * fx)
                );

            ELSE
                -- POST VENDOR PAYMENT
                lines := lines || jsonb_build_object(
                    'account', ap_acc,
                    'debit', (alloc.allocated_amount * fx),
                    'credit', 0
                );
            END IF;

            -- APPLY FX GAIN/LOSS LOGIC
            IF alloc.allocated_amount * fx <> alloc.allocated_base_amount THEN
                DECLARE diff numeric := (alloc.allocated_amount * fx) - alloc.allocated_base_amount;
                BEGIN
                    IF diff > 0 THEN
                        -- FX LOSS
                        lines := lines || jsonb_build_object(
                            'account', (
                                SELECT id FROM account_chart 
                                WHERE company_id = p.company_id AND code='560000'  -- FX losses
                            ),
                            'debit', diff,
                            'credit', 0
                        );
                    ELSE
                        -- FX GAIN
                        lines := lines || jsonb_build_object(
                            'account', (
                                SELECT id FROM account_chart 
                                WHERE company_id = p.company_id AND code='410000'  -- Other income
                            ),
                            'debit', 0,
                            'credit', abs(diff)
                        );
                    END IF;
                END;
            END IF;

        END LOOP;
    END;

    -- ============================================================
    -- POST USING GLOBAL LEDGER ENGINE
    -- ============================================================
    je := post_ledger_entry(
        p.tenant_id,
        p.company_id,
        p.journal_id,
        p.payment_date,
        p.currency,
        p.reference,
        lines
    );

    -- Link payment  journal entry
    IF p_payment_type = 'customer' THEN
        UPDATE customer_payment SET journal_entry_id = je WHERE id = p_payment_id;
    ELSE
        UPDATE vendor_payment SET journal_entry_id = je WHERE id = p_payment_id;
    END IF;

    RETURN je;
END;
$$;


