CREATE FUNCTION public.post_vendor_bill(p_bill_id uuid) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    b           record;             -- vendor bill
    item        record;             -- line item
    je          uuid;               -- final journal entry id
    lines       jsonb := '[]'::jsonb;

    ap_acc      uuid;
    expense_acc uuid;
    input_tax_acc uuid;
    rc_tax_acc uuid;  -- reverse charge liability

    taxrec      record;

    fx_rate numeric := 1;
    base_currency text;
    bill_currency uuid;
BEGIN
    -- Fetch bill
    SELECT * INTO b FROM vendor_bill WHERE id = p_bill_id;
    IF NOT FOUND THEN
        RAISE EXCEPTION 'Vendor bill % not found', p_bill_id;
    END IF;

    -- Company default accounts
    SELECT 
        ap_account_id,
        purchase_account_id,
        input_tax_account_id,
        output_tax_account_id
    INTO 
        ap_acc,
        expense_acc,
        input_tax_acc,
        rc_tax_acc
    FROM company_accounting_settings
    WHERE company_id = b.company_id;

    -- Currency
    SELECT code INTO base_currency
    FROM currencies c
    JOIN company_accounting_settings cas ON cas.currency_id = c.id 
    WHERE cas.company_id = b.company_id;

    SELECT currency_id INTO bill_currency
    FROM vendor_bill WHERE id = p_bill_id;

    -- FX rate
    IF bill_currency IS NOT NULL THEN
        fx_rate := fx_rate_lookup(
            (SELECT code FROM currencies WHERE id=bill_currency),
            base_currency,
            b.bill_date
        );
    END IF;

    -- ============================================================
    -- Expense Lines
    -- ============================================================
    FOR item IN SELECT * FROM vendor_bill_items WHERE bill_id = p_bill_id LOOP
        lines := lines || jsonb_build_object(
            'account', expense_acc,
            'debit', (item.amount * fx_rate),
            'credit', 0,
            'analytic_id', item.analytic_account_id
        );
    END LOOP;

    -- ============================================================
    -- TAX LINES
    -- ============================================================
    FOR taxrec IN 
        SELECT * 
        FROM vendor_bill_taxes 
        WHERE bill_id = p_bill_id
    LOOP
        -- Determine behavior (standard input vs reverse charge)
        DECLARE 
            behavior text;
            gl_input uuid;
            gl_output uuid;
            gl_rc uuid;
        BEGIN
            SELECT behavior INTO behavior 
            FROM tax_posting_rules
            WHERE company_id=b.company_id
              AND tax_rate_id=taxrec.tax_rate_id
            LIMIT 1;

            SELECT input_account_id, output_account_id, reverse_charge_account_id
            INTO gl_input, gl_output, gl_rc
            FROM tax_gl_accounts
            WHERE tax_rate_id = taxrec.tax_rate_id
              AND company_id = b.company_id;

            IF behavior = 'standard_input' THEN
                lines := lines || jsonb_build_object(
                    'account', gl_input,
                    'debit', (taxrec.tax_amount * fx_rate),
                    'credit', 0,
                    'tax_rate_id', taxrec.tax_rate_id,
                    'is_tax_line', true
                );

            ELSIF behavior = 'reverse_charge' THEN
                -- Debit Input tax
                lines := lines || jsonb_build_object(
                    'account', gl_input,
                    'debit', (taxrec.tax_amount * fx_rate),
                    'credit', 0,
                    'tax_rate_id', taxrec.tax_rate_id,
                    'is_tax_line', true
                );

                -- Credit Output tax
                lines := lines || jsonb_build_object(
                    'account', gl_rc,
                    'debit', 0,
                    'credit', (taxrec.tax_amount * fx_rate),
                    'tax_rate_id', taxrec.tax_rate_id,
                    'is_tax_line', true
                );
            END IF;

        END;
    END LOOP;

    -- ============================================================
    -- ACCOUNTS PAYABLE LINE
    -- ============================================================
    lines := lines || jsonb_build_object(
        'account', ap_acc,
        'debit', 0,
        'credit', (b.total_amount * fx_rate)
    );

    -- ============================================================
    -- POST JOURNAL
    -- Use the universal ledger engine (Module 8A)
    -- ============================================================
    je := post_ledger_entry(
        b.tenant_id,
        b.company_id,
        b.journal_id,
        b.bill_date,
        (SELECT code FROM currencies WHERE id = bill_currency),
        b.bill_number,
        lines
    );

    -- Link bill  journal entry
    UPDATE vendor_bill SET journal_entry_id = je WHERE id = p_bill_id;

    RETURN je;
END;
$$;


