-- TOC entry 891 (class 1255 OID 21492)
-- Name: reverse_journal_entry(uuid, date); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.reverse_journal_entry(p_entry uuid, p_date date DEFAULT (now())::date) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    je record;
    new_je uuid := gen_random_uuid();
    line record;
    seq text;
BEGIN
    -- Fetch original entry
    SELECT * INTO je FROM journal_entries WHERE id = p_entry;

    IF je.is_locked THEN
        RAISE EXCEPTION 'Cannot reverse a locked journal entry %', p_entry;
    END IF;

    -- Check period
    PERFORM check_period_open(je.company_id, p_date);

    -- Generate reversal number
    seq := generate_journal_sequence(je.journal_id);

    -- Create reversal JE
    INSERT INTO journal_entries (
        id, tenant_id, company_id, journal_id, entry_number,
        entry_date, currency_id, fx_rate,
        reference, is_posted
    ) VALUES (
        new_je, je.tenant_id, je.company_id, je.journal_id, seq,
        p_date, je.currency_id, je.fx_rate,
        CONCAT('Reversal of ', je.entry_number), true
    );

    -- Reverse lines
    FOR line IN
        SELECT * FROM journal_entry_lines WHERE journal_entry_id = p_entry
    LOOP
        INSERT INTO journal_entry_lines (
            id, journal_entry_id,
            tenant_id, company_id,
            account_id,
            debit, credit,
            tax_rate_id,
            analytic_account_id,
            is_tax_line
        )
        VALUES (
            gen_random_uuid(),
            new_je,
            line.tenant_id, line.company_id,
            line.account_id,
            line.credit,    -- swap
            line.debit,     -- swap
            line.tax_rate_id,
            line.analytic_account_id,
            line.is_tax_line
        );
    END LOOP;

    RETURN new_je;
END;
$$;


