-- TOC entry 894 (class 1255 OID 21502)
-- Name: run_fx_revaluation(uuid, uuid, date); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.run_fx_revaluation(p_tenant uuid, p_company uuid, p_reval_date date) RETURNS uuid
    LANGUAGE plpgsql
    AS $$
DECLARE
    run_id uuid := gen_random_uuid();
    base_currency text;
    row record;
    diff numeric;
    gain_acc uuid;
    loss_acc uuid;
    je uuid;
    lines jsonb := '[]'::jsonb;
BEGIN
    -- Save run
    INSERT INTO fx_revaluation_runs (id, tenant_id, company_id, revaluation_date, currency_id)
    VALUES (
        run_id,
        p_tenant,
        p_company,
        p_reval_date,
        (SELECT currency_id FROM company_accounting_settings WHERE company_id=p_company)
    );

    SELECT c.code INTO base_currency
    FROM currencies c
    JOIN company_accounting_settings cas ON cas.currency_id=c.id
    WHERE cas.company_id = p_company;

    -- FX accounts
    SELECT id INTO gain_acc 
    FROM account_chart 
    WHERE company_id=p_company AND code='410000';  -- Other income

    SELECT id INTO loss_acc 
    FROM account_chart 
    WHERE company_id=p_company AND code='560000';  -- FX Losses

    -- Process each open exposure
    FOR row IN 
        SELECT * FROM v_open_fx_exposure WHERE company_id=p_company
    LOOP
        diff := compute_fx_difference(
            row.amount,
            (SELECT code FROM currencies WHERE id=row.txn_currency_id),
            base_currency,
            row.txn_date,
            p_reval_date
        );

        IF diff = 0 THEN CONTINUE; END IF;

        -- Debit/Loss or Credit/Gain logic
        IF diff > 0 THEN
            -- LOSS:
            lines := lines || jsonb_build_object(
                'account', loss_acc,
                'debit', diff,
                'credit', 0
            ) || jsonb_build_object(
                'account', row.account_id,
                'debit', 0,
                'credit', diff
            );
        ELSE
            -- GAIN:
            lines := lines || jsonb_build_object(
                'account', row.account_id,
                'debit', abs(diff),
                'credit', 0
            ) || jsonb_build_object(
                'account', gain_acc,
                'debit', 0,
                'credit', abs(diff)
            );
        END IF;

    END LOOP;

    -- If no lines  no revaluation
    IF jsonb_array_length(lines) = 0 THEN
        RETURN run_id;
    END IF;

    -- POST ENTRY
    je := post_ledger_entry(
        p_tenant,
        p_company,
        (SELECT general_journal_id FROM company_accounting_settings WHERE company_id=p_company),
        p_reval_date,
        base_currency,
        CONCAT('FX Revaluation ', p_reval_date),
        lines
    );

    RETURN run_id;
END;
$$;


