--
-- TOC entry 863 (class 1255 OID 20167)
-- Name: set_setting(text, jsonb, uuid, uuid, uuid); Type: FUNCTION; Schema: public; Owner: threeg
--

CREATE FUNCTION public.set_setting(p_key text, p_value jsonb, p_tenant uuid DEFAULT NULL::uuid, p_company uuid DEFAULT NULL::uuid, p_actor uuid DEFAULT NULL::uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_id uuid;
  v_old_value jsonb;
  v_old_version int;
BEGIN
  PERFORM validate_json_schema(p_key, p_value);

  SELECT id, value, version INTO v_id, v_old_value, v_old_version
  FROM global_settings
  WHERE key = p_key
    AND tenant_id IS NOT DISTINCT FROM p_tenant
    AND company_id IS NOT DISTINCT FROM p_company
  FOR UPDATE;

  IF v_id IS NULL THEN
    INSERT INTO global_settings (tenant_id, company_id, key, value, scope, version, created_by, updated_by)
    VALUES (p_tenant, p_company, p_key, p_value,
            CASE WHEN p_company IS NOT NULL THEN 'company'
                 WHEN p_tenant IS NOT NULL THEN 'tenant'
                 ELSE 'global' END,
            1, p_actor, p_actor);

    PERFORM pg_notify('settings_changed',
      json_build_object('key', p_key, 'tenant_id', p_tenant, 'company_id', p_company)::text);

    RETURN;
  END IF;

  INSERT INTO global_settings_history (global_settings_id, tenant_id, company_id, key, old_value, new_value, changed_by)
  VALUES (v_id, p_tenant, p_company, p_key, v_old_value, p_value, p_actor);

  UPDATE global_settings
  SET value = p_value,
      version = v_old_version + 1,
      updated_by = p_actor,
      updated_at = now()
  WHERE id = v_id;

  PERFORM pg_notify('settings_changed',
    json_build_object('key', p_key, 'tenant_id', p_tenant, 'company_id', p_company)::text);

END;
$$;


