CREATE MATERIALIZED VIEW public.hms_product_batch_stock_mv AS
 SELECT b.id AS batch_id,
    b.product_id,
    b.tenant_id,
    b.company_id,
    b.batch_no,
    b.expiry_date,
    b.mrp,
